def linear_regression(x, y):
    b = ((sum(y) * sum(x**2)) - (sum(x) * sum(x*y)))/((len(x) * (sum(x**2))) - (sum(x))**2)  
    m = ((len(x) * sum(x*y)) - (sum(x) * sum(y)))/(((len(x) * (sum(x**2))) - (sum(x))**2))
    return (b, m)

def calc_cov(df):
    func = lambda x: x - x.mean()
    df_mean = df.apply(func)
    return (sum(df_mean.iloc[:,0] * df_mean.iloc[:,1]))/(df.shape[0] - 1)
